import React from "react";
import styled from "styled-components";
import { formatPrice } from "../utils/helpers";
import AmountButtons from "./AmountButtons";
import { FaTrash } from "react-icons/fa";
import { Link } from "react-router-dom";
import { useCartContext } from "../context/cart_context";
const CartItem = ({ id, image, name, color, price, amount, company }) => {
  const { toggleAmount } = useCartContext();
  const increase = () => {
    toggleAmount(id, "inc");
  };
  const decrease = () => {
    toggleAmount(id, "dec");
  };
  return (
    <Wrapper>
      <ul className="mainproductdiv">
        <li>
          <div className="row">
            <div>
              <img src={image} alt={name} className="small"></img>
            </div>
            <div className="cartname">
              <Link to={`/product/${name}`}>{name}</Link>
            </div>
            <div className="itemprice">${price}</div>
          </div>
        </li>
      </ul>
    </Wrapper>
  );
};
const Wrapper = styled.article` 
  width: 100%;
  align-items: center;   
  justify-content: space-between;          
  .cartname {   
    color: black;   
    font-weight: bold; 
    }
    .itemprice {   
      font-weight: bold;  } .delete {   color: white;   outline: none;   border: none; }  .card {   margin-top: 50px;   margin-left: 5px; }  .remove { outline: none; border: none; background: none; font-size: 25px; }  .block {   color: white; }  .card {   border: 1px solid black;   padding: 20px; }             } `;
export default CartItem;
