import React from 'react'
import styled from 'styled-components'
import { useCartContext } from '../context/cart_context'
import { formatPrice } from '../utils/helpers'
import { Link } from 'react-router-dom';
import {AiOutlineRight} from 'react-icons/ai';
const CartTotals = () => {
  const { total_amount, shipping_fee } = useCartContext()

  return (
    <Wrapper>
          <div class="promokodBox">
        <div class="promoBox1">
            <input class="inputPromo" placeholder="Promokod" type="text" /><button class="promoInputBtn" type="button" class="boxBtn">></button>
        </div>
        <table class="promoBox2">
            <tr className="promoDetBox">
                <td className="promoDetail">Endirim</td>
                <td className="promoPrice">{formatPrice(total_amount + shipping_fee)}</td>
            </tr>
            <tr className="promoDetBox">
                <td className="promoDetail">Çatdırılma</td>
                <td className="promoPrice">{formatPrice(total_amount + shipping_fee)}</td>
            </tr>
            <tr className="promoDetBox">
                <td className="promoDetail bigF">Toplam</td>
                <td className="promoPrice bigF">500 AZN</td>
            </tr>
        </table>
        <input className="promoSubmit" type="button" value="Sifarişi rəsmiləşdir"></input>
    </div>
      {/* <div classNameName='mainproceed'>
        <div classNameName='promo'>
