  button {
    border: none;
    outline: none;
    background: transparent;
  }

  .promo {
    width: 81%;
    margin: auto;
    display: flex;
    justify-content: space-between; 
    align-items: center;
    margin-top: 8px;
  }
   .promo button {
    background: white;
    border-radius: 5px;
    padding: 5px;
    border: 1px solid var(--black);
   }
  .promoinput {
    width: 240px;
    height: 30px;

  }

  .mainproceed {
    border: 1px solid black;
    border-radius: 10px;
  }
  article {
    border: 1px solid var(--clr-grey-8);
    border-radius: var(--radius);
    padding: 0rem 2.0rem;
    padding-bottom: 20px;
  }
  h4,
  h5,
  p {
    display: grid;
    grid-template-columns: 250px 1fr;
  }
  p {
    text-transform: capitalize;
  }
  h4,h5 {
    margin-top: 15px;
  }

  h4 {
    font-size: 24px;
    font-weight: bolder;
  }
  @media (min-width: 776px) {
    justify-content: flex-end;
  }
  .btn {
    width: 100%;
    margin-top: 1rem;
    text-align: center;
    font-weight: 700;
    border-radius: 10px;
  }
`

export default CartTotals
