import React from "react";
import styled from "styled-components";
import { formatPrice } from "../utils/helpers";
import AmountButtons from "./AmountButtons";
import { FaTrash } from "react-icons/fa";
import { Link } from "react-router-dom";
import { useCartContext } from "../context/cart_context";
const CartItem = ({ id, image, name, color, price, amount, company }) => {
  const { toggleAmount } = useCartContext();
  const increase = () => {
    toggleAmount(id, "inc");
  };
  const decrease = () => {
    toggleAmount(id, "dec");
  };


<div className="topcount">
<AmountButtons
  amount={amount}
  increase={increase}
  decrease={decrease}
/>

<div className="topcart">
  <h5 className="sub">{formatPrice(price * amount)}</h5>
</div>
</div>   





  return (
    <Wrapper>
      <ul className="mainproductdiv">
        <li>
          <div className="row">
            <div>
              <img src={image} alt={name} className="small"></img>
            </div>
            <div className="cartname">
              <Link to={`/product/${name}`}>{name}</Link>
            </div>
            <AmountButtons 
              amount={amount}
              increase={increase}
              decrease={decrease}
            />
            <div className="itemprice">${price}</div>
          </div>
        </li>
      </ul>
    </Wrapper>
  );
};
const Wrapper = styled.article` 
  width: 100%;
  display: flex;
  align-items: center;   
  justify-content: space-between;


  * {
    border: 1px solid green;
  }
  .row{
    width: 700px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    background: red;
  }
  .small {
    width: 70px;
    height: 70px;
  }
  .cartname {   
    color: black;   
    font-weight: bold; 
    }
    .itemprice {   
      font-weight: bold; 
       } 
  .card {
       margin-top: 50px;   
       margin-left: 5px; 
       }  

  .block { 
    color: white; 
    }  

  .card {   
    border: 1px solid black;   
    padding: 20px; }
    } 


  @media (max-width: 776px) {
    flex-direction: column;
    hr {
      width: 100%;
    }
  }


    `;


export default CartItem;
