import React, { useState } from 'react'
import styled from 'styled-components'
import { Link } from 'react-router-dom'
import { FaCheck } from 'react-icons/fa'
import { useCartContext } from '../context/cart_context'
import {AiOutlineHeart } from 'react-icons/ai'
import AmountButtons from './AmountButtons'
const AddToCart = ({ product }) => {
  // add to cart
  const { addToCart } = useCartContext()
  const { id, stock, colors } = product
  const [mainColor, setMainColor] = useState(colors[0])
  const [amount, setAmount] = useState(1)

  const increase = () => {
    setAmount((oldAmount) => {
      let tempAmount = oldAmount + 1
      if (tempAmount > stock) {
        tempAmount = stock
      }
      return tempAmount
    })
  }
  const decrease = () => {
    setAmount((oldAmount) => {
      let tempAmount = oldAmount - 1
      if (tempAmount < 1) {
        tempAmount = 1
      }
      return tempAmount
    })
  }
  return (
    <Wrapper>
      <div>



      </div>       
      <div className='btn-container'>
        {/* <AmountButtons
          increase={increase}
          decrease={decrease}
          amount={amount}
        /> */}


        <Link
          to='/cart'
          className='btn'
          onClick={() => addToCart(id, mainColor, amount, product)}
        >
          add to cart
        </Link>

        <button className='btn credit'>
       Hisse hisse odenis
        </button>

        <button className='like'>
          <AiOutlineHeart />
        </button>


      </div>
    </Wrapper>
  )
}
const Wrapper = styled.section`
  margin-top: 2rem;
  .colors {
    display: flex;
    flex-direction: column;
    span {
      text-transform: capitalize;
      font-weight: 700;
    }
    div {
      display: flex;
    }
  }

  .colorbtn {
    margin-top: 10px;
  }
  .color-btn {
    display: inline-block;
    width: 1.5rem;
    height: 1.5rem;
    border-radius: 50%;
    background: #222;
    margin-right: 0.5rem;
    border: none;
    cursor: pointer;
    opacity: 0.5;
    display: flex;
    align-items: center;
    justify-content: center;
    svg {
      font-size: 0.75rem;
      color: var(--clr-white);
    }
  }
  .active {
    opacity: 1;
  }
  .btn-container {
    height: 7vh;
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-top: 2rem;
    text-align: center;
    font-size: 18px;
  }

  .btn {
    background: var(--main-color);
    width: 210px;
    height: 30px;
  }

  .credit {
    background: white;
    color: var(--main-color);
    width: 210px;
    height: 30px;
    border: 1px solid var(--main-color);
  }

  .like {
    outline: none;
    border: none;
    font-size: 30px;
    background: transparent;
  }
`
export default AddToCart
