import React from "react";
import { Button, Form } from "react-bootstrap";
import styled from "styled-components";

const FormAccepting = () => {
  return (
    <Order>
      <div className="ordersection">
        <div className="form">
          <div className="options">
            <div className="cash option">
              <h3>Nağd</h3>
              <h5>Qapıda nağd ödəniş</h5>
            </div>
            <div className="cartop option">
              <h3>Kreditlə Ödəniş</h3>
              <h5>Kredit kartı ilə ödə</h5>
            </div>
          </div>

          <form>
            <div className="form-group">
              <div>
                <label for="name">Ad</label>
                <input
                  type="text"
                  className="form-control"
                  id="name"
                  
                />
              </div>

              <div>
                <label for="surname">Soyad</label>
                <input
                  type="text"
                  className="form-control"
                  id="surname"
                 
                />
              </div>
            </div>

            <div className="form-group">
              <div>
                <label for="name">Ata adı</label>
                <input
                  type="text"
                  className="form-control"
                  id="name"
                                  />
              </div>

              <div>
                <label for="surname">Telefon nömrəsi</label>
                <input
                  type="text"
                  className="form-control"
                  id="surname"
                  placeholder="+994"
                />
              </div>
            </div>

            <div className="latest">
              <label for="surname">Əlavə Qeydlər</label>
              <input
                type="text"
                className="form-control"
                id="surname"
                placeholder="Soyad"
              />
            </div>

            <h5>Çatdırılma Ünvanı</h5>
            <div className="form-group">
              <div>
                <label for="name">Ad</label>
                <input
                  type="text"
                  className="form-control"
                  id="name"
                  placeholder="Ad"
                />
              </div>

              <div>
                <label for="surname">Soyad</label>
                <input
                  type="text"
                  className="form-control"
                  id="surname"
                  placeholder="Soyad"
                />
              </div>
            </div>
            <div className="latest">
              <label for="surname">Soyad</label>
              <input
                type="text"
                className="form-control"
                id="surname"
                placeholder="Soyad"
              />
            </div>
          </form>
        </div>

        {/* <div className="checkoutform">
        <p>Cemi: 60</p>
        <p>Catdirilma: 30</p>
        <h3>Yekun: 600 $</h3>
      </div> */}
      </div>
    </Order>
  );
};

const Order = styled.div`
  .ordersection {
    width: 80%;
    margin: auto;
    border: none;
    margin-top: 70px;
    display: flex;
    justify-content: space-between;
  }

  .options {
    width: 100%;
    display: flex;
    justify-content: space-between;
  }
  .option {
    width: 300px;
    height: 130px;
    border: 1px solid #a3a3a3;
    box-sizing: border-box;
    border-radius: 10px;
    padding-left: 8px;
    padding-top: 8px;
  }

  .checkoutform {
    border: 1px solid black;
  }

  .form {
    width: 50%;
  }

  .form-group {
    width: 100%;
    display: flex;
    justify-content: space-between;
    margin-top: 20px;
  }

  .form-group label {
    font-weight: bolder;
  }
  .form-group input {
    width: 300px;
    height: 40px;
    outline: none;
    padding-left: 10px;

    background: #ffffff;
    border: 1px solid #a2a6b0;
    box-sizing: border-box;
    border-radius: 5px;
  }

  form h5 {
    margin-top: 40px;

    font-style: normal;
    font-weight: 500;
    font-size: 24px;
    line-height: 36px;
  }
  .form-group div:nth-child(2) {
    margin-left: 10px;
  }

  .latest {
    display: flex;
    flex-direction: column;
    margin-top: 10px;
  }

  .latest label {
    font-weight: bolder;
  }
  .latest input {
    width: 300px;
    height: 40px;
    outline: none;
    padding-left: 10px;
    background: #ffffff;
    border: 1px solid #a2a6b0;
    box-sizing: border-box;
    border-radius: 5px;
  }






  @media (max-width: 768px) {
    * {
      border: 1px solid green;
    }
    .form {
    width: 100%;
  }
    .ordersection {
    width: 100%;
    margin: auto;
    border: none;
    margin-top: 70px;
    display: flex;
    justify-content: space-between;
    background: red;
  }


  .options {
    width: 100%;
    display: flex;
    
    justify-content: space-between;
  }
  .option {
    width: 140px;
    height: 100px;
    border: 1px solid #a3a3a3;
    box-sizing: border-box;
    border-radius: 10px;
    padding-left: 8px;
    padding-top: 8px;

  }
  .option h3, h5{
    font-size: 10px;
  }
  

  .form-group {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    margin-top: 5px;
    padding: 10px;
  }

  .form-group label {
    font-weight: bolder;
  }
  .form-group input {
    width: 100%;
    height: 35px;
    outline: none;
    padding-left: 10px;
    margin-top: 5px;
    background: #ffffff;
    border: 1px solid #a2a6b0;
    box-sizing: border-box;
    border-radius: 5px;
  }

  .form-group div:nth-child(2) {
    margin-left: 0px;
  }

  .latest {
    width: 100%;
    display: flex;
    justify-content: center;
    margin-top: 20px;
    margin-auto;
    padding: 10px;

  }


  form h5 {
    margin-top: 40px;
    padding: 10px;
  }

  .latest label {
    float: left;
    text-align: left;
    font-weight: bolder;
  }
  .latest input {
    width: 100%;
    height: 35px;
    
    outline: none;
    padding-left: 10px;
    background: #ffffff;
    border: 1px solid #a2a6b0;
    box-sizing: border-box;
    border-radius: 5px;
  }

  }
`;

export default FormAccepting;
