import React, { useEffect } from 'react'
import { useParams, useHistory } from 'react-router-dom'
import { useProductsContext } from '../context/products_context'
import { single_product_url as url } from '../utils/constants'
import { formatPrice } from '../utils/helpers'

import {
  Loading,
  Error,
  ProductImages,
  AddToCart,
  PageHero,
  ProductDetails
} from '../components'
import styled from 'styled-components'


const SingleProductPage = () => {
  const { id } = useParams()
  const history = useHistory()
  console.log(history)
  const {
    single_product_loading: loading,
    single_product_error: error,
    single_product: product,
    fetchSingleProduct,
  } = useProductsContext()

  useEffect(() => {
    fetchSingleProduct(`${url}${id}`)
    // eslint-disable-next-line
  }, [id])
  useEffect(() => {
    if (error) {
      setTimeout(() => {
        history.push('/')
      }, 3000)
    }
    // eslint-disable-next-line
  }, [error])
  if (loading) {
    return <Loading />
  }
  if (error) {
    return <Error />
  }

  const {
    name,
    price,
    stock,
    id: sku,
    company,
    images,
  } = product
  return (
    <Wrapper>
      <PageHero />
      <div className='section section-center page'>
        <div className='product-center'>
          <ProductImages images={images} />
          <section className='content'>
            <h2>{name}</h2>
            {/* <Stars stars={stars} reviews={reviews} /> */}
            <p className='info b'>
              <span>Brand :</span>
              {company}
            </p>

            <h6 className='price'>{formatPrice(price)}</h6>

<hr />



            <h5 className='info'>
              <span>SKU :</span>
              {sku}
            </h5>
            <h5 className='info'>
              <span>SKU :</span>
              {sku}
            </h5>
            <h5 className='info'>
              <span>SKU :</span>
              {sku}
            </h5>
            <h5 className='info'>
              <span>SKU :</span>
              {sku}
            </h5>
            <h5 className='info'>
              <span>SKU :</span>
              {sku}
            </h5>
            <h5 className='info'>
              <span>SKU :</span>
              {sku}
            </h5>
            <hr />
            {stock > 0 && <AddToCart product={product} />}
          </section>
        </div>
      </div>

<div className='productdetails'>
<div className='topinfo'>
<h3 className='title'>Mehsul Detallari</h3>
<hr />

<div className='topinformation'>
    <div className='left'>
        <h2>Ses Texnologiyasi:</h2>
        <div>
        <p>Operativ Yaddas(Ram):</p>
         <p>8 GB</p>
        </div>
        <div>
        <p>Operativ Yaddas(Ram)</p>
         <p>8 GB</p>
        </div>
        <div>
        <p>Operativ Yaddas(Ram)</p>
         <p>8 GB</p>
        </div>
        <div>
        <p>Operativ Yaddas(Ram)</p>
         <p>8 GB</p>
        </div>
    </div> 




    <div className='left'>
        <h2>Ses Texnologiyasi</h2>
        <div>
        <p>Operativ Yaddas(Ram)</p>
         <p>LTE</p>
        </div>
        <div>
        <h5>Operativ Yaddas(Ram)</h5>
         <h5>8 GB</h5>
        </div>
        <div>
        <h5>Operativ Yaddas(Ram)</h5>
         <h5 >8 GB </h5>
        </div>
        <div>
        <h5>Operativ Yaddas(Ram)</h5>
         <h5>8 GB</h5>
        </div>
</div>
</div>

</div>
</div>



    </Wrapper>
  )
}

const Wrapper = styled.main`
  .product-center {
    display: grid;
    gap: 4rem;
    margin-top: 2rem;
  }
  .price {
    color: var(--main-color);
  }
  .desc {
    line-height: 2;
    max-width: 45em;
  }
  .info {
    text-transform: capitalize;
    width: 100px;
    display: grid;
    grid-template-columns: 85px 1fr;
    span {
      font-weight: 700;
    }
  }

  hr {
    margin-top: 10px;
  }
  .info2 {
    text-transform: capitalize;
    width: 100px;
    display: grid;
    grid-template-columns: 85px 1fr;
    span {
      font-weight: 700;
    }
  }
h5 {
  margin-top: 3px;
}



.topinfo {
    margin-top: 30px;
    width:90%;
    margin: auto;
}
.topinfo .title {
  text-align: left;
    color: #00a651;
    width: 90%;
    margin: auto;
}

.topinformation {
    width: 90%;
    margin: auto;
    display: flex;
    justify-content: space-between;
    margin-top: 20px;
}

.topinformation .left div {
    display: flex;
    padding: 0px;
    margin: 0px;
}

.left h2 {
  font-style: normal;
font-weight: 500;
font-size: 25px;
line-height: 37px;
}
.samples {
    display: flex;
    background-color: red;
    margin-top: 20px;
    /* justify-content: space-around; */
    padding-left: 10px;
}
.sampleproducts {
    display: flex;

}
.simpletitle {
    text-align: center;
    color: red;
}

  @media (min-width: 992px) {
    .product-center {
      grid-template-columns: 1fr 1fr;
      align-items: center;
    }
    .price {
      font-size: 1.25rem;
    }

    .b {
      margin-top: 10px;
    }


  }
`

export default SingleProductPage
